g run];		[task PostMsgToMain : msg];		}-(void)handleMainThreadMessage : (ARDuplexTaskMessage*)msg forTask : (ARDuplexTask*)task{		GrammarCheckTaskMessage *gmrMsg = (GrammarCheckTaskMessage*)msg;		DLog(@"GrammarCheckTask::handleMainThreadMessage : Sources == %@", gmrMsg.sourcesCode);		if(grammarDocument != nil)		{				[grammarDocument onGrammarCheckerCompleted : gmrMsg.parseResult];		}		[msg release];}@end